#include "register.h"
#include <string>

static const char *the_n = "";
static const char *the_e = "520867428764322287956";
static const char *the_d = "";

using namespace std;

Register::Register(QWidget *parent, Qt::WindowFlags flags)
	: QDialog(parent, flags)
    , _rsa(NULL)
{
	_ui.setupUi(this);
	connect(_ui.make, SIGNAL(clicked()), this, SLOT(on_make()));
	string n;
	string e;
	string d;
	for (int i = 0; i < strlen(the_n); i += 2)
    {
		n += the_n[i];
	}
	for (int i = 0; i < strlen(the_e); i += 3)
    {
		e += the_e[i];
	}
	for (int i = 0; i < strlen(the_d); i += 4)
    {
		d += the_d[i];
	}
    _rsa = RSA_new();

    _rsa->n = BN_new();
    _rsa->e = BN_new();
    _rsa->d = BN_new();

    BN_dec2bn(&(_rsa->n), n.c_str());
    BN_dec2bn(&(_rsa->e), e.c_str());
    BN_dec2bn(&(_rsa->d), d.c_str());
}

Register::~Register()
{
    RSA_free(_rsa);
}

void Register::on_make()
{
	//AutoSeededRandomPool rng;
	//RSA::PublicKey public_key(_params);
	//RSAES_OAEP_SHA_Encryptor e(public_key);
	//StringSource(plain, true, new PK_EncryptorFilter(rng, e, new StringSink(cipher)));
	//string base64;
	//StringSource(cipher, true, new Base64Encoder(new StringSink(base64)));

    string plain;
	plain.append(_ui.id_code->text().append("\n").toStdString());
	plain.append(_ui.commbox_id->text().append("\n").toStdString());
    plain.append(_ui.fist_time->dateTime().toString(Qt::ISODate).append("\n").toStdString());
	plain.append(_ui.expire_time->dateTime().toString(Qt::ISODate).append("\n").toStdString());
	plain.append("2043ED8E-4E35-4BFA-AC29-1756E553405D\n");
	plain.append(_ui.language->currentText().append("\n").toStdString());
    if (_ui.sdkCheckBox->isChecked())
    {
        plain.append("Do Forget Commit!\n");
    }

    int flen = RSA_size(_rsa);
    char *cipher = new char[flen];
    int length = RSA_public_encrypt(plain.length(), (const unsigned char*)plain.c_str(), 
        (unsigned char*)cipher, _rsa, RSA_PKCS1_OAEP_PADDING);

    size_t count = cyoBase64EncodeGetLength(length);
    char *base64 = new char[count];
    cyoBase64Encode(base64, cipher, length);

	_ui.output->setText(QString::fromUtf8(base64));

    delete []cipher;
    delete []base64;
}
